import React, { useEffect, useState } from "react";
import "../styles/Tareas.css";

const API_URL = "http://localhost/integradorweb/backend/api.php";

export default function Tareas() {
  const [tareas, setTareas] = useState([]);
  const [titulo, setTitulo] = useState("");
  const [descripcion, setDescripcion] = useState("");
  const [cargando, setCargando] = useState(true);
  const [error, setError] = useState(null);
  const [editId, setEditId] = useState(null);
  const [editTitulo, setEditTitulo] = useState("");
  const [editDescripcion, setEditDescripcion] = useState("");

  // Iniciar darkMode desde localStorage (si existe). Fallback: false
  const [darkMode, setDarkMode] = useState(() => {
    try {
      return localStorage.getItem("darkMode") === "true";
    } catch {
      return false;
    }
  });

  // Al montar: asegurarnos que body tenga la clase acorde a lo guardado
  useEffect(() => {
    if (darkMode) document.body.classList.add("dark-mode");
    else document.body.classList.remove("dark-mode");
    // nota: no guardamos aqu√≠ porque lo hace el otro useEffect que escucha darkMode
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []); // run once al montar

  // Aplicar / quitar la clase en body y persistir elecci√≥n en localStorage
  useEffect(() => {
    if (darkMode) {
      document.body.classList.add("dark-mode");
    } else {
      document.body.classList.remove("dark-mode");
    }
    try {
      localStorage.setItem("darkMode", darkMode);
    } catch {
      // Si falla el localStorage, no rompemos la app
    }
  }, [darkMode]);

  // Cargar tareas
  const cargarTareas = async () => {
    setCargando(true);
    setError(null);
    try {
      const res = await fetch(API_URL);
      if (!res.ok) throw new Error();
      const data = await res.json();
      setTareas(data);
    } catch {
      setError("Ups, no pudimos conectar üòû");
    } finally {
      setCargando(false);
    }
  };

  useEffect(() => {
    cargarTareas();
  }, []);

  // Crear tarea
  const agregarTarea = async (e) => {
    e.preventDefault();
    if (!titulo.trim()) {
      setError("T√≠tulo obligatorio");
      return;
    }
    try {
      const res = await fetch(API_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          titulo,
          descripcion,
          estado: "pendiente",
          prioridad: "media",
        }),
      });
      if (!res.ok) throw new Error();
      const nueva = await res.json();
      setTareas([nueva, ...tareas]);
      setTitulo("");
      setDescripcion("");
      setError(null);
    } catch {
      setError("Ups, no pudimos guardar la tarea üòû");
    }
  };

  // Borrar tarea
  const borrarTarea = async (id) => {
    try {
      await fetch(`${API_URL}?id=${id}`, { method: "DELETE" });
      setTareas(tareas.filter((t) => t.id !== id));
    } catch {
      setError("No se pudo eliminar la tarea üòû");
    }
  };

  // Marcar completada
  const marcarCompletada = async (tarea) => {
    const actualizado = { ...tarea, estado: "completada" };
    try {
      await fetch(`${API_URL}?id=${tarea.id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(actualizado),
      });
      setTareas(tareas.map((t) => (t.id === tarea.id ? actualizado : t)));
    } catch {
      setError("No se pudo actualizar la tarea üòû");
    }
  };

  // Iniciar edici√≥n
  const iniciarEdicion = (tarea) => {
    setEditId(tarea.id);
    setEditTitulo(tarea.titulo);
    setEditDescripcion(tarea.descripcion || "");
  };

  // Cancelar edici√≥n
  const cancelarEdicion = () => {
    setEditId(null);
    setEditTitulo("");
    setEditDescripcion("");
  };

  // Guardar edici√≥n
  const guardarEdicion = async (tarea) => {
    if (!editTitulo.trim()) {
      setError("T√≠tulo obligatorio");
      return;
    }
    const actualizado = {
      ...tarea,
      titulo: editTitulo,
      descripcion: editDescripcion,
    };
    try {
      await fetch(`${API_URL}?id=${tarea.id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(actualizado),
      });
      setTareas(tareas.map((t) => (t.id === tarea.id ? actualizado : t)));
      cancelarEdicion();
      setError(null);
    } catch {
      setError("No se pudo actualizar la tarea üòû");
    }
  };

  return (
    <div className="tareas-container">
      <div className="header" style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
        <h1>üìã Lista de Tareas</h1>

        {/* Bot√≥n toggle: type="button" para evitar submit accidental */}
        <button
          type="button"
          className="modo-btn"
          onClick={() => setDarkMode((prev) => !prev)}
          aria-pressed={darkMode}
          aria-label="Alternar tema claro u oscuro"
          title={darkMode ? "Cambiar a modo claro" : "Cambiar a modo oscuro"}
        >
          {darkMode ? "üåû Claro" : "üåô Oscuro"}
        </button>
      </div>

      {/* Mensajes */}
      {cargando && <p className="msg">Cargando...</p>}
      {error && <p className="msg error">{error}</p>}
      {!cargando && !error && tareas.length === 0 && (
        <p className="msg">No hay tareas</p>
      )}

      {/* Formulario agregar tarea */}
      <form onSubmit={agregarTarea} className="form-tarea">
        <input
          type="text"
          placeholder="T√≠tulo..."
          value={titulo}
          onChange={(e) => setTitulo(e.target.value)}
        />
        <textarea
          placeholder="Descripci√≥n (opcional)"
          value={descripcion}
          onChange={(e) => setDescripcion(e.target.value)}
        />
        <button type="submit">Agregar</button>
      </form>

      {/* Lista de tareas */}
      {tareas.length > 0 && (
        <ul className="lista-tareas">
          {tareas.map((t) => (
            <li key={t.id} className={t.estado === "completada" ? "done" : ""}>
              <div className="tarea-info">
                {editId === t.id ? (
                  <>
                    <input
                      type="text"
                      value={editTitulo}
                      onChange={(e) => setEditTitulo(e.target.value)}
                    />
                    <textarea
                      value={editDescripcion}
                      onChange={(e) => setEditDescripcion(e.target.value)}
                    />
                  </>
                ) : (
                  <>
                    <strong>{t.titulo}</strong>
                    {t.descripcion && <p>{t.descripcion}</p>}
                    {t.fecha_creacion && (
                      <small>
                        üïì Creada el{" "}
                        {new Date(t.fecha_creacion).toLocaleString()}
                      </small>
                    )}
                  </>
                )}
              </div>
              <div className="acciones">
                {editId === t.id ? (
                  <>
                    <button type="button" onClick={() => guardarEdicion(t)}>üíæ Guardar</button>
                    <button type="button" onClick={cancelarEdicion}>‚ùå Cancelar</button>
                  </>
                ) : (
                  <>
                    {t.estado !== "completada" && (
                      <button type="button" onClick={() => marcarCompletada(t)}>
                        ‚úÖ Completar
                      </button>
                    )}
                    <button type="button" onClick={() => iniciarEdicion(t)}>‚úèÔ∏è Editar</button>
                    <button type="button" onClick={() => borrarTarea(t.id)}>üóëÔ∏è Borrar</button>
                  </>
                )}
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
