/*
import React, { useEffect, useState } from "react";
import "../styles/Tareas.css";

const API_URL = "http://localhost/integradorweb/backend/api.php";

export default function Tareas() {
  const [tareas, setTareas] = useState([]);
  const [titulo, setTitulo] = useState("");
  const [descripcion, setDescripcion] = useState("");
  const [estado, setEstado] = useState("pendiente");
  const [cargando, setCargando] = useState(true);
  const [error, setError] = useState(null);

  // Cargar tareas
  
  
  useEffect(() => {
    fetch(API_URL)
      .then(res => res.json())
      .then(data => {
        setTareas(data);
        setCargando(false);
      })
      .catch(() => {
        setError("Ups, no pudimos conectar üòû");
        setCargando(false);
      });
  }, []);

  





  const agregarTarea = async (e) => {
    e.preventDefault();
    if (!titulo.trim()) {
      setError("T√≠tulo obligatorio");
      return;
    }

    try {
      const res = await fetch(API_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          titulo,
          descripcion,
          estado,
          prioridad: "media",
        }),
      });
      if (!res.ok) throw new Error();
      const nueva = await res.json();
      setTareas([{ id: nueva.id, titulo, descripcion, estado }, ...tareas]);
      setTitulo("");
      setDescripcion("");
      setError(null);
    } catch {
      setError("Ups, no pudimos guardar la tarea üòû");
    }
  };

  const borrarTarea = async (id) => {
    await fetch(`${API_URL}?id=${id}`, { method: "DELETE" });
    setTareas(tareas.filter(t => t.id !== id));
  };

  const marcarCompletada = async (tarea) => {
    const actualizado = { ...tarea, estado: "completada" };
    await fetch(`${API_URL}?id=${tarea.id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(actualizado),
    });
    setTareas(tareas.map(t => (t.id === tarea.id ? actualizado : t)));
  };

  if (cargando) return <p className="msg">Cargando...</p>;
  if (error) return <p className="msg error">{error}</p>;
  if (tareas.length === 0) return <p className="msg">No hay tareas</p>;

  return (
    <div className="tareas-container">
      <h1>üìã Lista de Tareas</h1>

      <form onSubmit={agregarTarea} className="form-tarea">
        <input
          type="text"
          placeholder="T√≠tulo..."
          value={titulo}
          onChange={(e) => setTitulo(e.target.value)}
        />
        <textarea
          placeholder="Descripci√≥n (opcional)"
          value={descripcion}
          onChange={(e) => setDescripcion(e.target.value)}
        />
        <button type="submit">Agregar</button>
      </form>

      {error && <p className="msg error">{error}</p>}

      <ul className="lista-tareas">
        {tareas.map(t => (
          <li key={t.id} className={t.estado === "completada" ? "done" : ""}>
            <div>
              <strong>{t.titulo}</strong>
              {t.descripcion && <p>{t.descripcion}</p>}
            </div>
            <div className="acciones">
              {t.estado !== "completada" && (
                <button onClick={() => marcarCompletada(t)}>‚úÖ Completar</button>
              )}
              <button onClick={() => borrarTarea(t.id)}>üóëÔ∏è Borrar</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}


